-- Create the table for value strategies
CREATE TABLE
  public.value_strategies (
    id BIGINT GENERATED BY DEFAULT AS IDENTITY NOT NULL,
    virality_axis TEXT NULL,
    content_method TEXT NULL,
    value_purpose TEXT NULL,
    practical_example TEXT NULL,
    CONSTRAINT value_strategies_pkey PRIMARY KEY (id)
  );

-- Enable Row Level Security on the new table
ALTER TABLE public.value_strategies ENABLE ROW LEVEL SECURITY;

-- Create a policy to allow authenticated users to read all rows
CREATE POLICY "Allow authenticated read access"
ON public.value_strategies
FOR SELECT
TO authenticated
USING (true);

